import React, { useState, useEffect, useReducer } from 'react';
import axios from 'axios';

const todo = props => {
  // just caling a function provided by react (useState)
  // const inputState = useState('');
  const [todoName, setTodoName] = useState('');
  const [submittedTodo, setSubmittedTodo] = useState(null);
  // const [todoList, setTodoList] = useState([]);

  const todoListReducer = (state, action) => {
    switch (action.type) {
      case 'ADD':
        return state.concat(action.payload);
      case 'SET':
        return action.payload;
      case 'REMOVE':
        return state.filter((todo) => todo.id !== action.payload.id);
      default:
        return state;
    }
  }

  const [todoList, dispatch] = useReducer(todoListReducer, []);

  // useEffect will execute when the function runs for the first time
  useEffect(() => {
    axios.get('https://reacthooks-b84d8.firebaseio.com/todos.json').then(result => {
      console.log(result);
      const todoData = result.data;
      const todos = [];
      for (const key in todoData) {
        todos.push({id: key, name: todoData[key].name})
      }
        dispatch({type: 'ADD', })
    });
    return () => {
      console.log('Cleanup');
    };
  }, []);

  const mouseMoveHandler = event => {
    console.log(event.clientX, event.clientY);
  };

  useEffect(() => {
    document.addEventListener('mousemove', mouseMoveHandler);
    return () => {
      document.removeEventListener('mousemove', mouseMoveHandler);
    };
  }, []);

  const inputChangeHandler = (event) => {
    setTodoName(event.target.value);
  };

  useEffect(
    () => {
      if (submittedTodo) {
        setTodoList(todoList.concat(submittedTodo));
      }
    },
    [submittedTodo]
  );

  // since we are setting new value, we override the old one
  // concat returns a new array
  const todoAddHandler = () => {
    setTodoList(todoList.concat(todoName));
    axios.post('https://reacthooks-b84d8.firebaseio.com/todos.json', {name: todoName})
      .then(res => {
        console.log(res)
        setTimeout(() => {
          const todoItem = {id: res.data.name, name: todoName};
          setSubmittedTodo(todoItem);
        }, 3000);
      })
      .catch(err => {
        console.log(err)
      });
  };
  //   setTodoState({
  //     userInput: todoState.userInput,
  //     todoList: todoState.todoList.concat(todoState.userInput)
  //   });
  // };

  return <React.Fragment>
    <input
       type="text"
       placeholder="Todo"
       onChange={inputChangeHandler}
       value={todoName}
       />
    <button type="button" onClick={todoAddHandler}>Add</button>
    <ul>
      {todoList.map(todo => (
         <li key={todo.id}>{todo.name}</li>
      ))}
    </ul>
  </React.Fragment>;
}

export default todo;

// useState tells us its a hook function
// can pass anything into useState, whatever state you want to manage
// gives first string as entire element inputState[0]
// inputSTate[] is the reference to a function and reference when onchange triggers
// function in a function is fine in JS
// you can't call useState in a function of a function
// cannot call useState in any form of nesting, only at the root
