import React, { useState} from 'react';

const todo = props => {
  // just caling a function provided by react (useState)
  // const inputState = useState('');

  const [todoName, setTodoName] = useState('');

  const inputChangeHandler = (event) => {
    setTodoName(event.target.value);
  };


  return <React.Fragment>
    <input
       type="text"
       placeholder="Todo"
       onChange={inputChangeHandler}
       value={todoName[0]}/>
    <button type="button">Add</button>
    <ul>

    </ul>
  </React.Fragment>;
}

export default todo;

// useState tells us its a hook function
// can pass anything into useState, whatever state you want to manage
// gives first string as entire element inputState[0]
// inputSTate[] is the reference to a function and reference when onchange triggers
// function in a function is fine in JS
